// Retrieve the input data from the previous node
const inputData = items[0].json;

// Define a function to check if a value is empty and set it to zero if it is
const checkEmptyAndSetToZero = (value) => {
    return value === undefined || value === null ? 0 : value;
};

// Iterate over each field in the input data
for (const fieldName in inputData) {
    if (inputData.hasOwnProperty(fieldName)) {
        // Check if the field value is empty and set it to zero if it is
        inputData[fieldName] = checkEmptyAndSetToZero(inputData[fieldName]);
    }
}

// Return the updated input data
return [{ json: inputData }];

const jsonData = items[0].json.body;

// Accessing the customer name from the JSON data
const customerName = jsonData["Customer Name"];

// Encoding the customer name
const encodedCustomerName = encodeURIComponent(customerName);

// Returning the encoded customer name
return [{ json: { encodedName: encodedCustomerName } }];
