// Initialize an empty object to store aggregated quantities
const aggregatedQuantities = {};

// Loop through each item in the input data
for (const item of items) {
    const data = item.json;

    // Retrieve item code and quantity from the current item
    const itemCode = data.item_code;
    const qty = data.qty;

    // If the item code is already in the aggregatedQuantities object, add the quantity to it
    if (aggregatedQuantities.hasOwnProperty(itemCode)) {
        aggregatedQuantities[itemCode] += qty;
    } else {
        // If the item code is not in the aggregatedQuantities object, initialize it with the quantity
        aggregatedQuantities[itemCode] = qty;
    }
}

// Initialize an empty array to store the output items
const outputItems = [];

// Iterate over the aggregatedQuantities object and create an output item for each item code with its aggregated quantity
for (const itemCode in aggregatedQuantities) {
    if (aggregatedQuantities.hasOwnProperty(itemCode)) {
        const aggregatedQty = aggregatedQuantities[itemCode];

        // Create an output item with item code and aggregated quantity
        const outputItem = {
            item_code: itemCode,
            qty: aggregatedQty
        };

        // Push the output item to the outputItems array
        outputItems.push(outputItem);
    }
}

// Return the array containing the list of all items with their aggregated quantities
return outputItems;


