// Initialize an empty array to store the output items
const outputItems = [];

// Accessing the items array passed from the split out node
const itemsArray = items;

// Looping through each item in the items array
for (const item of itemsArray) {
    // Accessing the data of each item
    const data = item.json;

    // Now you can access specific properties of each item's data and perform your calculations
    const rate = data.rate;
    const incoming_rate = data.incoming_rate;
    const item_code = data.item_code;
    const sales_invoice = data.parent;
    const item_group = data.item_group

    // Calculating percentage change for each item
    // const percentageChange = (((rate - incoming_rate) / rate) * 100).toFixed(2);
    const percentageChange = Math.round(((rate - incoming_rate) / rate) * 100 * 100) / 100;


    // Creating an object representing the output item for this iteration
    const outputItem = {
        item: item_code,
        sales_invoice: sales_invoice,
        item_group: item_group,
        percentageChange: percentageChange
    };

    // Pushing the outputItem to the outputItems array
    outputItems.push(outputItem);
}

// Returning the outputItems array containing the calculated percentage changes for each item
return outputItems;

