function findItemsNotInInvoice(salesInvoiceItems, salesOrderItems) {
    const itemsNotInInvoice = {};

    // Iterate over items in the sales order
    for (const [itemCode, quantity] of Object.entries(salesOrderItems)) {
        // If item code not present in sales invoice, add it to the result
        if (!(itemCode in salesInvoiceItems)) {
            itemsNotInInvoice[itemCode] = quantity;
        }
    }

    return itemsNotInInvoice;
}

